    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const objectsSection = document.querySelector('.objects');
            if (!objectsSection) return;

            const swiperContainer = objectsSection.querySelector('.swiper-container');
            const objectsWrapper = objectsSection.querySelector('#objectsWrapper');
            const originalSlides = Array.from(objectsWrapper.querySelectorAll('.swiper-slide'));

            // Сохраняем данные слайдов
            const slidesData = originalSlides.map(slide => {
                const objectItem = slide.querySelector('.objects__item');
                return {
                    element: slide,
                    productId: objectItem ? objectItem.dataset.productId : 'all'
                };
            });

            // Инициализация слайдера (будет выполнена в общем script.js)
            // Мы только добавим обработчик фильтрации

            // Фильтрация по продуктам
            const productFilterEl = objectsSection.querySelector('#productFilter');
            if (productFilterEl) {
                productFilterEl.addEventListener('change', function(e) {
                    const selectedValue = e.target.value;
                    const pagination = swiperContainer.querySelector('.custom-pagination');
                    objectsWrapper.innerHTML = '';

                    if (selectedValue === 'all') {
                        originalSlides.forEach(slide => {
                            objectsWrapper.appendChild(slide.cloneNode(true));
                        });
                    } else {
                        const filteredSlides = slidesData.filter(item => item.productId === selectedValue);
                        if (filteredSlides.length > 0) {
                            filteredSlides.forEach(item => {
                                objectsWrapper.appendChild(item.element.cloneNode(true));
                            });
                        } else {
                            objectsWrapper.innerHTML = `
                        <div class="text-small" style="width: 100%; text-align: center; padding: 20px;">
                            Нет объектов для выбранной системы отопления
                        </div>
                    `;
                            if (pagination) pagination.style.display = 'none';
                            return;
                        }
                    }

                    // Получаем экземпляр Swiper через общую функцию
                    const swiperInstance = window.getSwiperInstance(swiperContainer);

                    if (swiperInstance) {
                        // Обновляем слайдер после фильтрации
                        setTimeout(() => {
                            swiperInstance.update();
                            swiperInstance.slideTo(0);

                            // Обновляем пагинацию
                            if (swiperInstance.pagination && swiperInstance.pagination.render) {
                                swiperInstance.pagination.render();
                                swiperInstance.pagination.update();
                            }

                            const visibleSlides = objectsWrapper.querySelectorAll('.swiper-slide')
                                .length;
                            if (pagination) {
                                pagination.style.display = visibleSlides > swiperInstance.params
                                    .slidesPerGroup ? 'flex' : 'none';
                            }

                            // Перезапускаем анимацию через общие функции
                            if (window.resetProgressAnimation) {
                                window.resetProgressAnimation(swiperInstance);
                            }
                            if (window.startProgressAnimation) {
                                window.startProgressAnimation(swiperInstance);
                            }
                        }, 10);
                    }
                });
            }
        });
    </script>
